mapper.select:
select stId, stName, stAge, stPhoneNo, stGender, stClass, stHome
from student
where stId = #{stId}

mapper.delete:
delete
from student
where stId = #{stId, jdbcType=INTEGER}

mapper.select:
select stId, stName, stGender, stClass
from student
where stId = #{stId}

select stId, stName, stGender, stClass
from student
where stId = #{stId}

select stId, stName, stAge, stPhoneNo, stGender, stClass, stHome
from student
where stGender = #{stGender}
  and stName like #{stName}

select stId, stName, stAge, stPhoneNo, stGender, stClass, stHome
from student
where stGender = #{stGender}
  and stName like #{stName}
